	/**
	 * Preload REST API data for the gutenberg-boot page.
	 * Automatically called during page rendering.
	 */
	function gutenberg_boot_preload_data() {
		// Define paths to preload - same for all pages
		$preload_paths = array(
			'/?_fields=description,gmt_offset,home,name,site_icon,site_icon_url,site_logo,timezone_string,url,page_for_posts,page_on_front,show_on_front',
			array( '/wp/v2/settings', 'OPTIONS' ),
		);

		// Use rest_preload_api_request to gather the preloaded data
		$preload_data = array_reduce(
			$preload_paths,
			'rest_preload_api_request',
			array()
		);

		// Register the preloading middleware with wp-api-fetch
		wp_add_inline_script(
			'wp-api-fetch',
			sprintf(
				'wp.apiFetch.use( wp.apiFetch.createPreloadingMiddleware( %s ) );',
				wp_json_encode( $preload_data )
			),
			'after'
		);
	}
}

if ( ! function_exists( 'gutenberg_boot_render_page' ) ) {
	/**
	 * Render the gutenberg-boot page.
	 * Call this function from add_menu_page or add_submenu_page.
	 */
	function gutenberg_boot_render_page() {
		// Set current screen
		set_current_screen();

		// Remove unwanted deprecated handler
		remove_action( 'admin_head', 'wp_admin_bar_header' );

		// Remove unwanted scripts and styles that were enqueued during `admin_init`
		foreach ( wp_scripts()->queue as $script ) {
			wp_dequeue_script( $script );
		}
		foreach ( wp_styles()->queue as $style ) {
			wp_dequeue_style( $style );
		}

		// Fire init action for extensions to register routes and menu items
		do_action( 'gutenberg-boot_init' );

		// Preload REST API data
		gutenberg_boot_preload_data();

		// Get all registered routes and menu items
		$menu_items = get_gutenberg_boot_menu_items();
		$routes = get_gutenberg_boot_routes();

		// Get boot module asset file for dependencies
		$asset_file = plugin_dir_path( __FILE__ ) . '../../modules/boot/index.min.asset.php';
		if ( file_exists( $asset_file ) ) {
			$asset = require $asset_file;

			// This script serves two purposes:
			// 1. It ensures all the globals that are made available to the modules are loaded.
			// 2. It initializes the boot module as an inline script.
			wp_register_script( 'gutenberg-boot-prerequisites', '', $asset['dependencies'], $asset['version'], true );

			// Add inline script to initialize the app
			wp_add_inline_script(
				'gutenberg-boot-prerequisites',
				sprintf(
					'import("@wordpress/boot").then(mod => mod.init({mountId: "%s", menuItems: %s, routes: %s}));',
					'gutenberg-boot-app',
					wp_json_encode( $menu_items, JSON_HEX_TAG | JSON_UNESCAPED_SLASHES ),
					wp_json_encode( $routes, JSON_HEX_TAG | JSON_UNESCAPED_SLASHES )
				)
			);

			// Register prerequisites style by filtering script dependencies to find registered styles
			$style_dependencies = array_filter(
				$asset['dependencies'],
				function ( $handle ) {
					return wp_style_is( $handle, 'registered' );
				}
			);
			wp_register_style( 'gutenberg-boot-prerequisites', false, $style_dependencies, $asset['version'] );

			// Build dependencies for gutenberg-boot module
			$boot_dependencies = array(
				array(
					'import' => 'static',
					'id'     => '@wordpress/boot',
				),
			);

			// Add all registered routes as dependencies
			foreach ( $routes as $route ) {
				if ( isset( $route['route_module'] ) ) {
					$boot_dependencies[] = array(
						'import' => 'static',
						'id'     => $route['route_module'],
					);
				}
				if ( isset( $route['content_module'] ) ) {
					$boot_dependencies[] = array(
						'import' => 'dynamic',
						'id'     => $route['content_module'],
					);
				}
			}

			// Dummy script module to ensure dependencies are loaded
			wp_register_script_module(
				'gutenberg-boot',
				plugin_dir_url( __FILE__ ) . 'loader.js',
				$boot_dependencies
			);

			// Enqueue the boot scripts and styles
			wp_enqueue_script( 'gutenberg-boot-prerequisites' );
			wp_enqueue_script_module( 'gutenberg-boot' );
			wp_enqueue_style( 'gutenberg-boot-prerequisites' );
		}

		// Output the HTML
		?>
		<!DOCTYPE html>
		<html <?php language_attributes(); ?>>
		<head>
			<meta charset="<?php bloginfo( 'charset' ); ?>">
			<meta name="viewport" content="width=device-width, initial-scale=1">
			<title><?php echo esc_html( get_admin_page_title() ); ?></title>
			<style>
				html {
					background: #f1f1f1;
					color: #444;
					font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen-Sans, Ubuntu, Cantarell, "Helvetica Neue", sans-serif;
					font-size: 13px;
					line-height: 1.4em;
				}
				body {
					margin: 0;
				}
				#wpadminbar { display: none; }
			</style>
		<?php
		global $hook_suffix;
		// phpcs:ignore WordPress.WP.GlobalVariablesOverride.Prohibited
		$hook_suffix = 'gutenberg-boot';

		// BEGIN see wp-admin/admin-header.php
		print_admin_styles();
		print_head_scripts();

		/**
		 * Fires in head section for a specific admin page.
		 *
		 * @since 2.1.0
		 */
		do_action( "admin_head-{$hook_suffix}" ); // phpcs:ignore WordPress.NamingConventions.ValidHookName.UseUnderscores

		/**
		 * Fires in head section for all admin pages.
		 *
		 * @since 2.1.0
		 */
		do_action( 'admin_head' );
		// END see wp-admin/admin-header.php
		?>
		</head>
		<body class="gutenberg-boot">
			<div id="gutenberg-boot-app" style="height: 100vh; box-sizing: border-box;"></div>
		<?php
		// BEGIN see wp-admin/admin-footer.php

		/**
		 * Prints scripts or data before the default footer scripts.
		 *
		 * @since 1.2.0
		 */
		do_action( 'admin_footer', '' );

		// Print import map first so it's available for inline scripts
		wp_script_modules()->print_import_map();
		print_footer_scripts();
		wp_script_modules()->print_enqueued_script_modules();
		wp_script_modules()->print_script_module_preloads();
		wp_script_modules()->print_script_module_data();

		/**
		 * Prints scripts or data after the default footer scripts.
		 *
		 * @since 2.8.0
		 */
		do_action( "admin_footer-{$hook_suffix}" ); // phpcs:ignore WordPress.NamingConventions.ValidHookName.UseUnderscores
		// END see wp-admin/admin-footer.php
		?>
		</body>
		</html>
		<?php
		exit;
	}
}

if ( ! function_exists( 'gutenberg_boot_intercept_render' ) ) {
	/**
	 * Intercept admin_init to render the page early.
	 * This bypasses the default WordPress admin template.
	 */
	function gutenberg_boot_intercept_render() {
		// phpcs:ignore WordPress.Security.NonceVerification.Recommended
		if ( isset( $_GET['page'] ) && 'gutenberg-boot' === $_GET['page'] ) {
			gutenberg_boot_render_page();
			exit;
		}
	}
}

// Hook the interceptor to admin_init
add_action( 'admin_init', 'gutenberg_boot_intercept_render' );
